log-expression.expression:
al Map<String, dynamic> params;

  const ChatScreen(this.params, {Key? key}) : super(key: key);

  @override
  _ChatScreenState createState() => _ChatScreenState();
}

class _ChatScreenState extends State<ChatScreen> with TickerProviderStateMixin {
  ChatHead chatHead = ChatHead();
  late Product product;
  late ThemeData theme;
  late CustomTheme ct;
  late ScrollController _scrollC;
  late TextEditingController _txtC;
  late FocusNode _focusNode;

  final MainController _mainC = Get.find<MainController>();
  final List<String> _menuChoices = [
    'Delete chat',
    'Report User',
    'Block User',
  ];
  final List<Timer> _timers = [];

  List<ChatMessage> _msgs = [];
  bool _inputEmpty = true;
  bool _disposed = false, _listenerBusy = false;
  late Future<void> _initFuture;
  late AnimationController _fadeC;

  // Enhanced multimedia support
  final ImagePicker _imagePicker = ImagePicker();
  AudioRecorder? _audioRecorder;
  AudioPlayer? _audioPlayer;
  String? _recordingPath;
  bool _isRecording = false;
  Map<String, VideoPlayerController> _videoControllers = {};
  bool _showMultimediaOptions = false;

  @override
  void initState() {
    super.initState();

    theme = AppTheme.theme;
    ct = AppTheme.customTheme;
    _scrollC = ScrollController();
    if (widget.params.containsKey('start_message')) {
      start_message = widget.params['start_message'].toString().trim();
    }

    _txtC = TextEditingController(text: start_message)
      ..addListener(() => setState(() => _inputEmpty = _txtC.text.isEmpty));
    _txtC.text = start_message;
    _txtC.selection = TextSelection.fromPosition(
      TextPosition(offset: _txtC.text.length),
    );
    _focusNode = FocusNode();

    _fadeC = AnimationController(
      vsync: this,
      duration: const Duration(milliseconds: 300),
      lowerBound: 0.8,
      upperBound: 1.0,
    )..forward();

    // Initialize multimedia components
    _audioRecorder = AudioRecorder();
    _audioPlayer = AudioPlayer();

    _initFuture = _initialize();
  }

  @override
  void dispose() {
    _disposed = true;
    _scrollC.dispose();
    _txtC.dispose();
    _focusNode.dispose();
    _fadeC.dispose();
    for (var t in _timers) t.cancel();

    // Dispose multimedia components
    _audioRecorder?.dispose();
    _audioPlayer?.dispose();

    // Dispose video controllers
    for (var controller in _videoControllers.values) {
      controller.dispose();
    }
    _videoControllers.clear();

    super.dispose();
  }

  List<String> accepted_tasks = ['START_CHAT'];

  String task = '';
  String receiver_id = '';
  String sender_id = '';
  String start_message = '';

  Future<void> _initialize() async {
    if (!widget.params.containsKey('receiver_id')) {
      Utils.toast('Receiver ID not found.');
      Get.back();
      return;
    }
    if (!widget.params.containsKey('chatHead')) {
      if (widget.params['chatHead'].runtimeType == chatHead.runtimeType) {
        chatHead = widget.params['chatHead'];
        _markAsRead();
      }
    }

    receiver_id = widget.params['receiver_id'];
    if (receiver_id.isEmpty) {
      Utils.toast('Receiver ID is empty.');
      Get.back();
      return;
    }
    if (_mainC.userModel.id < 1) {
      await _mainC.getLoggedInUser();
    }
    if (_mainC.userModel.id < 1) {
      Utils.toast('Logged in user not found.');
      Get.back();
      return;
    }

    sender_id = _mainC.userModel.id.toString();
    if (sender_id.isEmpty) {
      Utils.toast('Sender ID is empty.');
      Get.back();
      return;
    }
    sender_id = sender_id.trim();
    receiver_id = receiver_id.trim();
    String product_id = '';

    if (sender_id == receiver_id) {
      Utils.toast('Sender and receiver IDs are the same.');
      Get.back();
      return;
    }

    if (widget.params.containsKey('task')) {
      if (widget.params['task'] == 'PRODUCT_CHAT') {
        if (widget.params['product'].runtimeType != ((Product()).runtimeType)) {
          return;
        }
        product = widget.params['product'];
        product_id = product.id.toString();

        List<ChatHead> temp_chats = await ChatHead.get_items(
          _mainC.userModel,
          where:
              ' product_owner_id = $receiver_id AND customer_id = $sender_id AND product_id = $product_id ',
        );

        if (temp_chats.isEmpty) {
          temp_chats = await ChatHead.get_items(
            _mainC.userModel,
            where:
                ' product_owner_id = $sender_id AND customer_id = $receiver_id AND product_id = $product_id ',
          );

          if (temp_chats.isNotEmpty) {
            chatHead = temp_chats[0];
          }
        }
      }
    } else if (chatHead.id < 1) {
      List<ChatHead> temp_chats = await ChatHead.get_items(
        _mainC.userModel,
        where: ' product_owner_id = $receiver_id AND customer_id = $sender_id',
      );

      if (temp_chats.isEmpty) {
        temp_chats = await ChatHead.get_items(
          _mainC.userModel,
          where:
              ' product_owner_id = $sender_id AND customer_id = $receiver_id',
        );

        if (temp_chats.isNotEmpty) {
          chatHead = temp_chats[0];
        }
      }
    }

    _markAsRead();

    if (chatHead.id < 1) {
      RespondModel? resp;
      Utils.showLoader(false);
      try {
        resp = RespondModel(
          await Utils.http_post('chat-start', {
            'sender_id': sender_id,
            'receiver_id': receiver_id,
            'proChatsScreenduct_id': product_id,
          }),
        );
      } catch (e) {
        Utils.toast('Failed to start chat because: $e');
        //pop
        Get.back();
        return;
      } finally {
        Utils.hideLoader();
      }

      if (resp.code != 1) {
        Utils.toast(resp.message);
        Get.back();
        return;
      }

      ChatHead tempHead = ChatHead.fromJson(resp.data);
      if (tempHead.id < 1) {
        Utils.toast('Failed to parse chat');
        Get.back();
        return;
      }

      chatHead = tempHead;
      _markAsRead();
      setState(() {});
    }

    if (chatHead.id < 1) {
      await _startChat();
      if (chatHead.id < 1) {
        Utils.toast2('Failed to start chat');
        Get.back();
        return;
      }
    }

    _msgs = await ChatMessage.get_items(
        _mainC.userModel,
        where: 'chat_head_id = ${chatHead.id}',
      )
      ..sort((a, b) => a.id.compareTo(b.id));

    _markAsRead();

    _pollLoop();
    setState(() {});
    task = widget.params['task'];

    _scrollToBottom();
  }

  Future<void> _startChat() async {
    Utils.toast2('Please waitâ€¦', is_long: true);
    if (!await Utils.is_connected()) {
      Utils.toast2('No internet connection');
      return;
    }
    // Chat starting logic can be enhanced later if needed
  }

  Future<void> _markAsRead() async {
    if (chatHead.id < 1) {
      return;
    }
    RespondModel r = RespondModel(
      await Utils.http_post('chat-mark-as-read', {
        'receiver_id': chatHead.customer_id,
        'chat_head_id': chatHead.id,
      }),
    );

    if (r.code == 1) {
      await chatHead.save();
    } else {
      Utils.toast(r.message);
    }

    await ChatHead.getLocalData();
    await ChatHead.getLocalData();
  }

  void _pollLoop() {
    if (_disposed || _listenerBusy) return;
    _listenerBusy = true;
    ChatMessage.getOnlineItems(
      _mainC.userModel,
      params: {'chat_head_id': chatHead.id, 'doDeleteAll': true},
    ).then((_) async {
      final all = await ChatMessage.getLocalData(
          _mainC.userModel,
          where: 'chat_head_id = ${chatHead.id}',
        )
        ..sort((a, b) => a.id.compareTo(b.id));
      if (all.length > _msgs.length) {
        setState(() => _msgs = all);
        _scrollToBottom();
      }
      _listenerBusy = false;
      if (!_disposed) Future.delayed(const Duration(seconds: 5), _pollLoop);
    });
  }

  Future<void> _onRefresh() => _initialize();

  Future<void> _sendMessage() async {
    final text = _txtC.text.trim();
    if (text.isEmpty) return Utils.toast("Can't send empty");

    final me = _mainC.userModel.id.toString();
    final other =
        me == chatHead.product_owner_id
            ? chatHead.customer_id
            : chatHead.product_owner_id;

    ChatMessage msg =
        ChatMessage()
          ..chat_head_id = chatHead.id.toString()
          ..sender_id = me
          ..receiver_id = other
          ..body = text
          ..type = 'text'
          ..created_at = DateTime.now().toIso8601String()
          ..updated_at = DateTime.now().toIso8601String()
          ..isMyMessage = true;

    _txtC.clear();
    setState(() => _inputEmpty = true);
    _showMultimediaOptions = false;
    _msgs.add(msg);

    _scrollToBottom();

    // keep keyboard open
    _focusNode.requestFocus();
    await msg.save();
    doSendMessage(msg);
  }

  // Enhanced _sendMessage method for multimedia
  Future<void> _sendMultimediaMessage({
    required String messageType,
    required String content,
    String? photoUrl,
    String? audioUrl,
    String? videoUrl,
    String? latitude,
    String? longitude,
  }) async {
    final me = _mainC.userModel.id.toString();
    final other =
        me == chatHead.product_owner_id
            ? chatHead.customer_id
            : chatHead.product_owner_id;

    // Add message to local list immediately
    ChatMessage msg =
        ChatMessage()
          ..chat_head_id = chatHead.id.toString()
          ..sender_id = me
          ..receiver_id = other
          ..body = content
          ..type = messageType
          ..photo = photoUrl ?? ''
          ..audio = audioUrl ?? ''
          ..longitude = longitude ?? ''
          ..latitude = latitude ?? ''
          ..created_at = DateTime.now().toIso8601String()
          ..updated_at = DateTime.now().toIso8601String()
          ..isMyMessage = true;

    setState(() {
      _msgs.add(msg);
      _showMultimediaOptions = false;
    });

    _scrollToBottom();
    await msg.save();

    try {
      Map<String, dynamic> requestData = {
        'receiver_id': other,
        'content': content,
        'message_type': messageType,
      };

      // Add multimedia specific fields
      if (photoUrl != null) requestData['photo'] = photoUrl;
      if (audioUrl != null) requestData['audio'] = audioUrl;
      if (videoUrl != null) requestData['video'] = videoUrl;
      if (latitude != null) requestData['latitude'] = latitude;
      if (longitude != null) requestData['longitude'] = longitude;

      final response = await Utils.http_post('chat-send', requestData);
      final resp = RespondModel(response);

      if (resp.code == 1) {
        msg.status = 'sent';
        await msg.save();
      } else {
        print('Failed to send $messageType message: ${resp.message}');
        Utils.toast('Failed to send $messageType message: ${resp.message}');
        msg.status = 'failed';
        await msg.save();
      }
    } catch (e) {
      Utils.toast('Error sending $messageType message: $e');
      msg.status = 'failed';
      await msg.save();
    }
  }

  void _scrollToBottom() {
    WidgetsBinding.instance.addPostFrameCallback((_) {
      if (_scrollC.hasClients) {
        _scrollC.animateTo(
          _scrollC.position.maxScrollExtent,
          duration: const Duration(milliseconds: 300),
          curve: Curves.easeOut,
        );
      }
    });
  }

  Future<void> doSendMessage(ChatMessage msg) async {
    // Send message to server using enhanced API format
    try {
      final resp = RespondModel(
        await Utils.http_post('chat-send', {
          'receiver_id': msg.receiver_id,
          'content': msg.body,
          'message_type': msg.type,
        }),
      );

      if (resp.code == 1) {
        // Message sent successfully, update local status
        msg.status = 'sent';
        await msg.save();
      } else {
        // Handle error
        Utils.toast('Failed to send message: ${resp.message}');
        msg.status = 'failed';
        await msg.save();
      }
    } catch (e) {
      // Handle network error
      Utils.toast('Failed to send message: $e');
      msg.status = 'failed';
      await msg.save();
    }
  }
